import path from "path";
import fs from "fs";
import os from "os";
registerAllCommands();
var currentWorkingDirectory = process.cwd();
async function executeShellCommand(command) {
  const trimmedCmd = command.trim();
  if (trimmedCmd === "cd" || trimmedCmd.startsWith("cd ")) {
    let targetDir;
    if (trimmedCmd === "cd" || trimmedCmd === "cd ~") {
      targetDir = os.homedir();
    } else if (trimmedCmd === "cd -") {
      return {
        success: false,
        output: "cd -: OLDPWD not set",
        exitCode: 1
      };
    } else {
      const targetPath = trimmedCmd.slice(3).trim();
      const expandedPath = targetPath.startsWith("~") ? path.join(os.homedir(), targetPath.slice(1)) : targetPath;
      targetDir = path.resolve(currentWorkingDirectory, expandedPath);
    }
    try {
      const stat2 = fs.statSync(targetDir);
      if (!stat2.isDirectory()) {
        return {
          success: false,
          output: `cd: not a directory: ${targetDir}`,
          exitCode: 1
        };
      }
      currentWorkingDirectory = targetDir;
      return {
        success: true,
        output: targetDir,
        exitCode: 0
      };
    } catch {
      return {
        success: false,
        output: `cd: no such file or directory: ${targetDir}`,
        exitCode: 1
      };
    }
  }
  if (trimmedCmd === "pwd") {
    return {
      success: true,
      output: currentWorkingDirectory,
      exitCode: 0
    };
  }
  try {
    const proc = Bun.spawn(["bash", "-c", trimmedCmd], {
      cwd: currentWorkingDirectory,
      stdout: "pipe",
      stderr: "pipe"
    });
    const stdout = await new Response(proc.stdout).text();
    const stderr = await new Response(proc.stderr).text();
    await proc.exited;
    const output = [stdout.trim(), stderr.trim()].filter(Boolean).join(`
`);
    return {
      success: proc.exitCode === 0,
      output: output || "(no output)",
      exitCode: proc.exitCode ?? 1
    };
  } catch (error2) {
    return {
      success: false,
      output: error2 instanceof Error ? error2.message : String(error2),
      exitCode: 1
    };
  }
}
function createMCPContext() {
  const output = [];
  return {
    setOutput: (lines) => {
      output.length = 0;
      output.push(...lines);
    },
    appendOutput: (line) => {
      output.push(line);
    },
    clearOutput: () => {
      output.length = 0;
    },
    navigateToMenu: () => {},
    toast: (_type, _message) => {},
    getRobotState: () => ({
      robot: null,
      teleop: null,
      teleopSessionActive: false
    }),
    getDatasetState: () => ({
      datasets: [],
      activeDataset: null
    })
  };
}
async function main() {
  const server = new Server({
    name: "maker",
    version: "0.1.0"
  }, {
    capabilities: {
      tools: {}
    }
  });
  server.setRequestHandler(ListToolsRequestSchema, async () => {
    const commands = commandRegistry.getAll();
    const shellTools = [
      {
        name: "shell_exec",
        description: "Execute a shell command in the current working directory. Supports all bash commands. Use 'cd' to change directories, 'pwd' to show current directory. The working directory persists across calls.",
        inputSchema: {
          type: "object",
          properties: {
            command: {
              type: "string",
              description: "The shell command to execute (e.g., 'ls -la', 'cat file.txt', 'cd src')"
            }
          },
          required: ["command"]
        }
      },
      {
        name: "shell_cwd",
        description: "Get the current working directory for shell commands",
        inputSchema: {
          type: "object",
          properties: {},
          required: []
        }
      },
      {
        name: "shell_ls",
        description: "List files and directories in the current or specified directory",
        inputSchema: {
          type: "object",
          properties: {
            path: {
              type: "string",
              description: "Optional path to list (defaults to current directory)"
            },
            all: {
              type: "boolean",
              description: "Include hidden files (dotfiles)"
            },
            long: {
              type: "boolean",
              description: "Use long listing format with details"
            }
          },
          required: []
        }
      },
      {
        name: "shell_read",
        description: "Read the contents of a file",
        inputSchema: {
          type: "object",
          properties: {
            path: {
              type: "string",
              description: "Path to the file to read (relative to cwd or absolute)"
            }
          },
          required: ["path"]
        }
      },
      {
        name: "shell_write",
        description: "Write content to a file (creates or overwrites)",
        inputSchema: {
          type: "object",
          properties: {
            path: {
              type: "string",
              description: "Path to the file to write (relative to cwd or absolute)"
            },
            content: {
              type: "string",
              description: "Content to write to the file"
            }
          },
          required: ["path", "content"]
        }
      }
    ];
    const makerTools = commands.map((cmd) => ({
      name: `maker_${cmd.name}`,
      description: cmd.description,
      inputSchema: {
        type: "object",
        properties: (cmd.args || []).reduce((acc, arg) => ({
          ...acc,
          [arg.name]: {
            type: arg.type || "string",
            description: arg.description,
            ...arg.choices ? { enum: arg.choices } : {}
          }
        }), {}),
        required: (cmd.args || []).filter((arg) => arg.required).map((arg) => arg.name)
      }
    }));
    return {
      tools: [...shellTools, ...makerTools]
    };
  });
  server.setRequestHandler(CallToolRequestSchema, async (request) => {
    const { name, arguments: args } = request.params;
    if (name === "shell_exec") {
      const command2 = args?.command;
      if (!command2) {
        return {
          content: [{ type: "text", text: "Missing required argument: command" }],
          isError: true
        };
      }
      const result = await executeShellCommand(command2);
      return {
        content: [
          {
            type: "text",
            text: `[cwd: ${currentWorkingDirectory}]
[exit: ${result.exitCode}]

${result.output}`
          }
        ],
        isError: !result.success
      };
    }
    if (name === "shell_cwd") {
      return {
        content: [{ type: "text", text: currentWorkingDirectory }]
      };
    }
    if (name === "shell_ls") {
      const { path: targetPath, all, long } = args || {};
      const flags = [long ? "-l" : "", all ? "-a" : ""].filter(Boolean).join("");
      const lsCommand = `ls ${flags} ${targetPath || "."}`.trim();
      const result = await executeShellCommand(lsCommand);
      return {
        content: [{ type: "text", text: result.output }],
        isError: !result.success
      };
    }
    if (name === "shell_read") {
      const filePath = args?.path;
      if (!filePath) {
        return {
          content: [{ type: "text", text: "Missing required argument: path" }],
          isError: true
        };
      }
      const fullPath = path.resolve(currentWorkingDirectory, filePath);
      try {
        const content = fs.readFileSync(fullPath, "utf-8");
        return {
          content: [{ type: "text", text: content }]
        };
      } catch (error2) {
        return {
          content: [
            {
              type: "text",
              text: `Error reading file: ${error2 instanceof Error ? error2.message : String(error2)}`
            }
          ],
          isError: true
        };
      }
    }
    if (name === "shell_write") {
      const { path: filePath, content } = args || {};
      if (!filePath) {
        return {
          content: [{ type: "text", text: "Missing required argument: path" }],
          isError: true
        };
      }
      if (content === undefined) {
        return {
          content: [{ type: "text", text: "Missing required argument: content" }],
          isError: true
        };
      }
      const fullPath = path.resolve(currentWorkingDirectory, filePath);
      try {
        const dir = path.dirname(fullPath);
        if (!fs.existsSync(dir)) {
          fs.mkdirSync(dir, { recursive: true });
        }
        fs.writeFileSync(fullPath, content, "utf-8");
        return {
          content: [{ type: "text", text: `Successfully wrote ${content.length} bytes to ${fullPath}` }]
        };
      } catch (error2) {
        return {
          content: [
            {
              type: "text",
              text: `Error writing file: ${error2 instanceof Error ? error2.message : String(error2)}`
            }
          ],
          isError: true
        };
      }
    }
    if (!name.startsWith("maker_")) {
      return {
        content: [{ type: "text", text: `Unknown tool: ${name}` }],
        isError: true
      };
    }
    const commandName = name.replace(/^maker_/, "");
    const command = commandRegistry.get(commandName);
    if (!command) {
      return {
        content: [
          {
            type: "text",
            text: `Unknown command: ${commandName}`
          }
        ],
        isError: true
      };
    }
    const argList = [];
    if (args && command.args) {
      for (const argDef of command.args) {
        const value = args[argDef.name];
        if (value !== undefined) {
          argList.push(String(value));
        }
      }
    }
    const context = createMCPContext();
    try {
      const result = await command.execute(argList, context);
      if (result.success) {
        return {
          content: [
            {
              type: "text",
              text: result.output?.join(`
`) || "Command executed successfully"
            }
          ]
        };
      } else {
        return {
          content: [
            {
              type: "text",
              text: result.error || "Command failed"
            }
          ],
          isError: true
        };
      }
    } catch (error2) {
      return {
        content: [
          {
            type: "text",
            text: `Error executing command: ${error2 instanceof Error ? error2.message : String(error2)}`
          }
        ],
        isError: true
      };
    }
  });
  const transport = new StdioServerTransport;
  await server.connect(transport);
  console.error("Maker MCP server started");
}
main().catch((error2) => {
  console.error("Fatal error:", error2);
  process.exit(1);
});
